    if (!Array.isArray(elements)) {
      elements = [elements];
    }

    elements.forEach(function(els) {
      Object.keys(els).forEach(function(e) {
        el = typeof els[e] === 'string' ? { selector: els[e] } : els[e];
        el.parent = parent;
        el.name = e;
        elementObjects[el.name] = new Element(el);
